select.if:
SELECT * FROM qrCode 
 WHERE date(concat(substring(qrCode,1,4),"-",substring(qrCode,5,2),"-",substring(qrCode,7,2))) >= date(#{startJumun}) AND date(concat(substring(qrCode,1,4),"-",substring(qrCode,5,2),"-",substring(qrCode,7,2))) <= date(#{endJumun}) 
order by idx desc limit #{startIndexNo},#{pageSize};
