mapper.select:
SELECT middle.*,main.categoryMainName AS categoryMainName
  FROM categoryMiddle middle, categoryMain main WHERE middle.categoryMainCode=main.categoryMainCode
  ORDER BY middle.categoryMiddleCode desc;

SELECT sub.*,main.categoryMainName as categoryMainName,middle.categoryMiddleName as categoryMiddleName
  FROM categorySub sub, categoryMiddle middle, categoryMain main WHERE sub.categoryMiddleCode=middle.categoryMiddleCode and sub.categoryMainCode=main.categoryMainCode
  ORDER BY sub.categorySubCode desc;

SELECT product.*, main.categoryMainName, middle.categoryMiddleName, sub.categorySubName
  FROM dbProduct product, categoryMain main, categoryMiddle middle, categorySub sub 
  WHERE productName=#{productName} ORDER BY idx DESC LIMIT 1;

select.if:
SELECT count(*)
from dbOrder a join dbBaesong b using(orderIdx) 
 where b.mid=#{mid} and date(orderDate) >= date(#{startJumun}) and date(orderDate) <= date(#{endJumun}) order by orderDate desc ;

SELECT count(*)
from dbOrder a join dbBaesong b using(orderIdx) 
 where b.mid=#{mid} and date(orderDate) >= date(#{startJumun}) and date(orderDate) <= date(#{endJumun}) and b.orderStatus=#{conditionOrderStatus} order by orderDate desc ;

mapper.select:
SELECT oder.*,baesong.* 
from dbOrder oder join dbBaesong baesong using(orderIdx) 
where baesong.mid=#{mid} and date(orderDate) = substring(now(),1,10)
order by baesong.idx desc 
limit #{startIndexNo},#{pageSize};

select.if:
SELECT o.*,b.* 
from dbOrder o join dbBaesong b using(orderIdx) 
 where b.mid=#{mid} and date(orderDate) >= date(#{startJumun}) and date(orderDate) <= date(#{endJumun})  
order by b.idx desc 
limit #{startIndexNo},#{pageSize};

SELECT o.*,b.* 
from dbOrder o join dbBaesong b using(orderIdx) 
 where b.mid=#{mid} and date(orderDate) >= date(#{startJumun}) and date(orderDate) <= date(#{endJumun}) and b.orderStatus=#{conditionOrderStatus}  
order by b.idx desc
limit #{startIndexNo},#{pageSize};

mapper.select:
SELECT count(*)
from dbOrder a join dbBaesong b using(orderIdx) 
 where b.mid=#{mid} and b.orderStatus=#{orderStatus} order by orderDate desc ;

select.if:
SELECT o.*,b.* 
FROM dbOrder o JOIN dbBaesong b USING(orderIdx) 
 WHERE b.mid=#{mid}  
ORDER BY b.idx DESC
LIMIT #{startIndexNo},#{pageSize};

SELECT o.*,b.* 
FROM dbOrder o JOIN dbBaesong b USING(orderIdx) 
 WHERE b.mid=#{mid} and b.orderStatus=#{orderStatus}  
ORDER BY b.idx DESC
LIMIT #{startIndexNo},#{pageSize};

mapper.select:
SELECT count(*)
FROM dbOrder o JOIN dbBaesong b USING(orderIdx) 
 WHERE b.mid=#{mid} AND date(orderDate) >= date(subdate(now(), INTERVAL #{conditionDate} DAY)) AND date(orderDate) <= date(now()) ;
